import React from "react";
import { Container, Header, Footer, Sidebar } from "../components/layout/layout";
import { Button, Card, Modal } from "../components/ui/ui";
import { DynamicForm, Field, formUtils } from "../components/forms/forms";

function App() {    
    return (
    <div className="wrapper">
      <Header />

      <div style={{ display: "flex", flex: 2 }}>
        <Sidebar />

        <Container>
          <h2>Welcome!</h2>

          {/* Card with "Create" button */}
          <Card>
            <h3>Create a New Hack</h3>
            <Button onClick={() => alert("BOOOM!")} style={{ marginTop: "5px" }}>
              0_o Create
            </Button>
          </Card>

          {/* Scrollable area */}
          <div style={{ height: "500px", overflowY: "scroll", marginTop: "2px", border: "1px solid #481414ff", padding: "10px" }}>
            <p>Here will be scrollable content...</p>
            <p>For example, a list of projects or messages</p>
            <p>You can add more blocks...</p>
            <p>...</p>
            <p>End of scroll</p>
          </div>

          {/* Authorization forms */}
          <div style={{ marginTop: "20px" }}>
            {/* You can add forms here */}
          </div>
        </Container>
      </div>

      <Footer />
    </div>
  );
}
export default App;
