import React from "react";
import { Container, Header, Footer, Sidebar } from "../components/layout/layout";
import { Button, Card, Modal } from "../components/ui/ui";
import { DynamicForm, Field, formUtils } from "../components/forms/forms";

function App() {    
    return (
    <div className="wrapper" style={{ display: "flex", flexDirection: "column", minHeight: "100vh" }}>
      <Header />

      <div style={{ display: "flex", flex: 1 }}>
        <Sidebar />

        <Container>
          <h2>Добро пожаловать!</h2>

          {/* Карточка с кнопкой "Создать" */}
          <Card>
            <h3>Создать новый проект</h3>
            <Button onClick={() => alert("Создать!")} style={{ marginTop: "10px" }}>
              Создать
            </Button>
          </Card>

          {/* Прокрутка */}
          <div style={{ height: "300px", overflowY: "scroll", marginTop: "20px", border: "1px solid #ccc", padding: "10px" }}>
            <p>Здесь будет прокручиваемый контент...</p>
            <p>Например список проектов или сообщений</p>
            <p>Можно добавить ещё блоки...</p>
            <p>...</p>
            <p>Конец прокрутки</p>
          </div>

          {/* Формы авторизации */}
          <div style={{ marginTop: "20px" }}>
            
          </div>
        </Container>
      </div>

      <Footer />
    </div>
  );
}
export default App;
